INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (711,357,1,'<a href=\'https://euromillions.com/en/facts/euromillions-drawing-times\'>EuroMillions draws</a> take place on Tuesday and Friday evenings at 21:00 CET (20:00 GMT) in Paris. Check out the difference with your time zone to know what time they play in your country and don\'t miss the opportunity to buy the tickets for any draw!','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (712,357,2,'<a href=\'https://euromillions.com/es/datos/euromillones-horario-sorteo\'>Los sorteos de EuroMillones</a> se realizan dos veces por semana los martes y viernes por la noche, a las 21:00 (CET) en Pars. El sorteo se acaba alrededor de las 22:30 (CET).','es');
