INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (715,358,1,'To win the EuroMillions jackpot players need to match all 5 main numbers and 2 Lucky Stars. But to win the minimum EuroMillions prize, players just have to match 2 or more numbers. It is that easy to be a winner!','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (716,358,2,'Para ganar el bote de EuroMillones, los jugadores deben acertar todos los 5 nmeros principales y las 2 estrellas, pero para ganar el premio mnimo de EuroMillones, los jugadores solo deben acertar 2 o ms nmeros. Es muy fcil ser un ganador!','es');
