INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (719,359,1,'The latest <a href=\'/euromillions/results\'>EuroMillions results</a> are revealed approximately an hour following the draw 22:30 CET. A full breakdown of prizes is released another hour later 23:30 CET. However, in the event of a large jackpot the processing and verification of the latest results can take up until midnight to finalise. You can get the latest Euromillions results on our results page.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (720,359,2,'Los ltimos <a href=\'/es/euromillions/resultados\'>resultados de EuroMillones</a> se revelan aproximadamente una hora despus del sorteo, 22:30 CET. El listado completo de premios es publicado otra hora despus, 23:30 CET. Sin embargo, en el evento de un gran bote, el proceso de verificacin de los ltimos resultados puede finalizar hasta la medianoche. Los resultados ms recientes de Euromillones estn disponibles en nuestra pgina de resultados.','es');
