INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (723,360,1,'The EuroMillions lottery has a total of 13 prize tiers. The highest prize is won matching the 5 main numbers and the 2 Lucky stars, while the smallest just with 2 out 5 main numbers. You can check out the <a href=\'/euromillions/results\'>prize breakdown of the latest draw in our results page</a>, with the amount of winners and money won per prize tier, as well as the prize breakdown of <a href=\'/euromillions/results/draw-history-page\'>all past EuroMillions draws</a>.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (724,360,2,'La lotera EuroMillones tiene un total de 13 categoras de premios. La distribucin de premios muestra las ganancias por cada categora. Puedes ver en <a href=\'/es/euromillions/resultados\'>nuestros resultados</a> un ejemplo de la distribucin de premios.','es');
