INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (727,361,1,'The minimum guaranteed jackpot is 17 million. It can rollover for an unlimited number of consecutive draws until it reaches the jackpot cap of 190 million.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (728,361,2,'El bote mnimo garantizado es de 17 millones. Puede acumularse hasta 190 millones.','es');
