INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3285,479,4,'Completa una combinazione dell\'EuroMillions selezionando <em>cinque</em> numeri principali dall\'1 al 50 e <em>due</em> numeri stella dall\'1 al 12 per ogni linea. In alternativa, clicca \'Numeri random in tutte le linee\' per generare una sequenza casuale di numeri.','it');
