INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3287,484,4,'Visualizzerai 6 linee per giocata in uno schermo standard di computer fisso o portatile (di meno se accedi con uno schermo pi piccolo, con un tablet o un cellulare). Per aggiungere pi linee, clicca su \'Aggiungi linee\' e appariranno nuove combinazioni di gioco.','it');
