INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3293,487,4,'Immediatamente dopo l\'estrazione riceverai un\'email con il dettaglio degli ultimi risultati, in cui ti sar comunicato se hai vinto. Tutti le vincite verranno automaticamente accreditate sul tuo account di giocatore.','it');
