INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3358,637,4,'EuroMillions.com divulga i dati personali su ordine delle autorit governative e / o in base alla normativa vigente. Inoltre, la Societ si riserva il diritto di divulgare i dati personali ai destinatari rilevanti quando la Societ ha ragionevoli motivi per sospettare le irregolarit che coinvolgono un conto scommesse bet-at-home.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3359,638,4,'EuroMillions.com si riserva il diritto di trattare i dati personali per finalit di CRM (Customer Relationship Management).','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3360,694,4,'Valuta','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3361,695,4,'La valuta operativa del sito EuroMillions.com  l\'EURO. Le transazioni di addebito del prezzo di partecipazione e il pagamento del montepremi ai vincitori saranno effettuati in EURO.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3362,696,4,'In ogni caso in cui un\'estrazione ufficiale della lotteria viene condotto in una valuta diversa dall\'EURO, per la pubblicazione sul sito web EuroMillions.com del valore dei premi in tale estrazione, verr effettuata una conversione approssimativa dalla valuta in cui dovrebbero essere pagati all\'EURO.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3363,697,4,'Tutti gli scambi da diverse valute a EURO saranno effettuati utilizzando i tassi di conversione correnti che vengono periodicamente pubblicati dagli istituti bancari.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3364,698,4,'Le vincite in valute diverse dall\'Euro verranno prima convertite in EURO sulla base dell\'ultimo tasso di cambio pubblicato periodicamente e poi accreditate sul conto del giocatore.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3365,699,4,'Il sito web EuroMillions.com ha il diritto di detrarre le spese ragionevoli derivanti dai costi di cambio valuta.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3366,610,4,'Dichiarazione dei partecipanti','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3367,611,4,'Con l\'accettazione dei presenti Termini e Condizioni, il partecipante dichiara che, in base alle leggi del paese in cui  cittadino, ha diritto a partecipare al gioco che si svolge su questo sito. Il partecipante dichiara inoltre che, durante l\'utilizzo di questo sito Web, lui / lei agisce per conto suo e che non vi  altra persona che sia rappresentata da lui / lei.','it');
