INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3458,688,4,'Il partecipante sar il solo e pienamente responsabile per la segnalazione e il pagamento di tutte le tasse e gli oneri derivanti o imposti sugli importi depositati, pagati, trasferiti o prelevati dal suo account giocatore.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3459,689,4,'180/5000\r\nIl partecipante  consapevole e riconosce che gli operatori ufficiali delle lotterie potrebbero trattenere una parte del premio e inoltrarlo all\'autorit fiscale competente per suo conto.','it');
