INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3468,607,4,'Prima di registrarsi o partecipare a qualsiasi attivit attraverso il sito EuroMillions.com,  responsabilit di ciascun partecipante assicurarsi che rispetti ogni legge applicabile sullo/a stesso/a.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3469,608,4,'A causa dei limiti menzionati dalle leggi e regole della regolamentazione sopracitata, una persona che NON ha diritto a partecipare ai giochi o viola le suddette norme non ha diritto nemmeno ad alcun premio ottenuto attraverso il sito EuroMillions.com. ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3470,609,4,'Il sito web EuroMillions.com, i suoi agenti o qualsiasi altra persona a suo nome non realizzano rappresentazioni o garanzie, esplicite o implicite, per quanto riguarda la legittimit di partecipazione di qualsiasi persona a qualsiasi attivit attraverso il suddetto sito web EuroMillions.com e il suo diritto legale a partecipare a qualsiasi altra attivit proposta sul sito EuroMillions.com. ','it');
