INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3472,618,4,'Ciascun utente avr accesso al proprio conto di giocatore. L\'accesso al conto del giocatore  possibile solo attraverso credenziali valide. Senza credenziali valide, il conto del giocatore non sar disponibile. ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3473,619,4,'Gli utenti possono modificare i loro dati di identificazione, vedere le proprie transazioni, le scommesse, le impostazioni di notifica e realizzare versamenti e prelievi attraverso il proprio conto di giocatore. ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3474,620,4,'I conti del giocatore sono ad uso esclusivo e personale dell\'utente. L\'utente si impegna a non creare un conto per nessun altro o usare il conto di terzi. I conti dei giocatori non sono soggetti ad alcun tipo di trasferimento o assegnazione. L\'utente sar l\'unico responsabile per qualsiasi e ciascuna delle attivit del suo conto. ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3475,621,4,'L\'utente, con la presente, riconosce e dichiara che egli/ella  l\'unico proprietario di tutto il denaro trasferito sul proprio conto e che tutto il denaro trasferito possiede un titolo legale chiaro.  ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3476,622,4,'EuroMillions.com pu in qualsiasi momento, senza preavviso alcuno, rifiutare a propria esclusiva discrezione il deposito o il trasferimento di denaro proveniente da qualsiasi conto dei suoi utenti. ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3477,690,4,'Prezzi','it');
