INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3479,692,4,'Il prezzo di partecipazione a ciascun gioco sul sito web di EuroMillions.com pu variare da un\'estrazione ad un\'altra. ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3480,693,4,'In ciascun evento il prezzo di partecipazione del sito web EuroMillions.com  il prezzo che appare nel Biglietto e nel Cestino. L\'importo finale per la partecipazione  l\'importo che appare nel modello di conferma di pagamento e nel conto del partecipante. ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3481,612,4,'Registrazione ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3482,613,4,'La registrazione  gratuita e obbligatoria per utilizzare i servizi del sito web EuroMillions.com. I Partecipanti devono assicurarsi che i loro dati siano reali e devono informare immediatamente EuroMillions.com per qualsiasi variazione dei dati della carta di credito o del conto bancario, modificando i propri conti nel suddetto sito web. ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3483,614,4,'Il partecipante possiede l\'intera responsabilit di riservare la confidenzialit dell\'identificazione e i dettagli di accesso al sito web EuroMillions.com e di non metterli a disposizione di nessun altro. ','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3484,615,4,'Le tecnologie attualmente disponibili sono in grado solamente di permettere al sito web EuroMillions.com di verificare l\'accuratezza e la validit delle credenziali di accesso al sito web EuroMillions.com, ma non sono adatte per verificare la propriet legale e l\'uso delle stesse. Pertanto, la totale responsabilit per l\'uso non autorizzato di dati di identificazione e accesso dell\'utente ricade esclusivamente sul titolare. ','it');
