INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3490,707,4,'In nessun caso il sito web EuroMillions.com e/o chiunque altro abbia agito per suo conto (compresi i contabili, i revisori dei conti, ecc.) saranno responsabili per qualsiasi danno diretto, indiretto, incidentale, speciale o danni per perdita di profitti, entrate, dati o utilizzo sostenuti da qualsiasi partecipante o terza parte, derivanti dall\'accesso o utilizzo del sito web EuroMillions.com.','it');
