INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3567,938,1,'Friday night\'s draws are the favorites of the lottery players, because on these days extraordinary situations such as the SuperDraw happen. Indeed, more than 80% of the winners of the EuroMillions jackpot have bought their tickets for a Friday draw. As always the draw time is 21:00 CET (20:00 GMT).','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3568,938,2,'El viernes es el da preferido de los jugadores de EuroMillions, ya que en esos das se suelen dar situaciones extraordinarias como los SuperDraw. De hecho, ms del 80% de los ganadores del bote de EuroMillions compraron su boleto para un sorteo del viernes. Como siempre, el sorteo se realiza a las 21:00.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3569,938,3,' -        ,         !  80%           !  ,     22.45   !','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3570,938,4,'Il venerd  il giorno preferito dai giocatori dell\'EuroMillions, dato che spesso si verificano eventi eccezionali come i SuperDraw. Infatti, piu dell\'80% dei vincitori del jackpot dell\'EuroMillions comprarono il biglietto per un\'estrazione del venerd. Come sempre, l\'estrazione si svolge alle 21:00 (CET).','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3571,936,1,'EuroMillions ticket sale closes at 19:00 (CET) the same day of the draw. You will be able to purchase tickets for the next draw shortly after the latest one has taken place. Don\'t miss the chance to be our next EuroMillionaire and <a href=\'https://euromillions.com/euromillions/play\' >buy your tickets now on EuroMillions.com</a>!','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3572,936,2,'La venta de boletos de EuroMillones se cierra el mismo da del sorteo a las 19:00 (CET). Podrs comprar boletos para el siguiente sorteo poco despus de que se revelen los resultados del ltimo. <a href=\'https://euromillions.com/es/euromillions/jugar\' >Compra tus boletos en EuroMillions.com</a> y no pierdas la oportunidad de ser nuestro prximo EuroMillonario!','es');
