INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3574,936,4,'La vendita di biglietti dell\'EuroMillions si chiude il giorno dell\'estrazione alle 19:00. Puoi comprare biglietti per la prossima estrazione poco dopo la pubblicazione dei risultati dell\'ultima. <a href=\'https://euromillions.com/it/euromillions/gioca\' >Compra i tuoi biglietti su EuroMillions.com</a> e non perdere l\'occasione di essere il nostro prossimo EuroMilionario!','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3575,940,1,'Although the chances of winning are the same on any draw day, EuroMillions players usually purchase less tickets on Tuesdays. Precisely for this reason, in several Tuesday night\'s draws the EuroMillions jackpot has been taken by a single winning ticket.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3576,940,2,'Aunque hay las mismas posibilidades de ganar en cualquier da de los sorteos, los jugadores de EuroMillones usualmente compran menos boletos los martes. Precisamente por eso, en muchos sorteos del martes el bote de EuroMillions se lo ha llevado un solo boleto ganador.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3577,940,3,'        ,        ,    .    ,          !\r\n','ru');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3578,940,4,'Anche se ci sono le stesse probabilit di vincere in qualsiasi giorno della lotteria, i giocatori soitamente comprano meno biglietti per l\'EuroMillions del marted. Proprio per questo, in molte estrazioni del marted il jackpot dell\'EuroMillions  stato assegnato a un solo biglietto vincente.','it');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3579,932,1,'Euromillions draws take place twice a week every Tuesday and Friday at 21:00 CET (20:00 GMT) in Paris. In each draw 5 numbers abd 2 lucky stars are selected. The latest winning numbers of the lottery are revealed on our <a href=\'/euromillions/results\'>EuroMillions results page</a> around 22:30 CET (21:30 GMT) of the draw day.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3580,932,2,'Los sorteos de EuroMillones se realizan dos veces por semana los martes y viernes a las 21:00 (CET) en Pars. En cada sorteo se extraen 5 numeros y 2 estrellas. Los ltimos numeros ganadores de la lotera son publicados en nuestra <a href=\'https://euromillions.com/es/euromillions/resultados\' >pgina de resultados de EuroMillones</a> alrededor de las 22:30 (CET) del mismo da.','es');
