INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3582,932,4,'Le estrazioni dell\'EuroMillions si svolgono due volte a settimana di marted e venerd alle 21:00 a Parigi. In ogni estrazione si selezionano 5 numeri e 2 stelle. Gli ultimi numeri vincenti della lotteria vengono pubblicati sulla nostra <a href=\'https://euromillions.com/it/euromillions/estrazioni\' >pagina di risultati dell\'EuroMillions</a> intorno alle 22:30 della stessa sera.','it');
