INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (3997,450,5,'Euromillions.com is de enige eigenaar van de informatie die op deze website wordt verzameld. Wij zullen deze informatie niet verkopen, delen of verhuren aan derden op manieren die niet in deze verklaring worden vermeld.','nl');
