INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4026,625,5,'Het bedrijf houdt zich aan de Wet Bescherming Persoonsgegevens, aan andere relevante voorschriften, aan juridische en / of soortgelijke bankbiljetten op de plaats van het contract, rekening houdend met de Richtlijn Gegevensbescherming (Richtlijn 95/46 / EG) en de Richtlijn Communicatieprivacy elektronisch (EG-richtlijn) / 2002/58 / EG).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4027,626,5,'Euromillions.com zorgt ervoor dat hun accounthouders te allen tijde:','nl');
