INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4029,636,5,'alle redelijke maatregelen worden getroffen om gegevens in te vullen, te corrigeren, te blokkeren of te wissen voor zover die gegevens onvolledig of onjuist zijn, rekening houdend met de doeleinden waarvoor ze worden verwerkt.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4030,628,5,'alle persoonlijke gegevens eerlijk en rechtmatig worden verwerkt;','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4031,629,5,'alle persoonlijke gegevens altijd in overeenstemming worden verwerkt volgens goede werkwijzen;','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4032,630,5,'persoonlijke gegevens alleen worden verzameld voor specifieke, expliciet vermelde en legitieme doeleinden;','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4033,631,5,'persoonlijke gegevens niet worden verwerkt voor doeleinden die onverenigbaar zijn met die waarvoor de informatie wordt verzameld;','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4034,632,5,'persoonsgegevens die worden verwerkt, adequaat en relevant zijn in relatie tot de doeleinden van de verwerking;','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4035,633,5,'er geen persoonsgegevens meer worden verwerkt dan noodzakelijk is met het oog op de doeleinden van de verwerking;','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4036,634,5,'de persoonlijke gegevens die worden verwerkt, correct zijn en, indien nodig, actueel;','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4037,635,5,'persoonlijke gegevens worden niet bewaard voor een langere periode dan nodig is, met betrekking tot de doeleinden waarvoor ze worden verwerkt; en','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4038,637,5,'Euromillions.com maakt persoonlijke gegevens bekend wanneer dit wordt bevolen door de bevoegde autoriteiten en / of op grond van bepalingen uit de toegepaste wet. Bovendien behoudt het bedrijf zich het recht voor om persoonlijke gegevens aan relevante ontvangers te verstrekken wanneer het bedrijf gegronde redenen heeft om overtredingen van de wet te vermoeden. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4039,638,5,'Euromillions.com behoudt zich het recht voor om persoonsgegevens te verwerken voor CRM (Customer Relationship Management) doeleinden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4040,694,5,'Valuta','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4041,695,5,'De operationele valuta van de Euromillions.com website is de EURO. Debettransacties van de deelnameprijs en de betaling van het prijzengeld aan de winnaars worden in EURO gedaan.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4042,696,5,'In het geval dat een officile loterijtrekking wordt uitgevoerd in een andere valuta dan de EURO, zal er een geschatte omrekening van de valuta worden gemaakt om de prijzen op de juiste manier in de EURO te publiceren.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4043,697,5,'Alle omrekeningen van verschillende valuta naar EURO worden uitgevoerd met behulp van de huidige wisselkoersen die periodiek door de bankinstellingen worden gepubliceerd.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4044,698,5,'Winsten in andere valuta\'s dan EURO worden omgerekend naar EURO op basis van de laatste periodiek gepubliceerde wisselkoers en vervolgens op de account van de speler bijgeschreven.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4045,699,5,'De website Euromillions.com heeft het recht om kosten in verband met de wisselkoers in mindering te brengen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4046,610,5,'Verklaringen van deelnemers','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4047,611,5,'Met het accepteren van de Algemene Voorwaarden, verklaart de deelnemer dat, volgens de wetten van zijn / haar eigen land waar hij / zij een burger is, de deelnemer het recht heeft om deel te nemen aan het spel die hij / zij op deze website speelt. De deelnemer verklaart ook dat hij / zij tijdens het gebruik van deze website in naam van zichzelf handelt en dat er geen andere persoon is die door hem of haar wordt vertegenwoordigd.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4048,571,5,'DEFINITIES','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4049,572,5,'Toegangsinformatie verwijst naar de unieke inloggegevens gegenereerd tijdens het registratieproces.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4050,581,5,'Ticket verwijst naar het online ticket dat is samengesteld op de Euromillions.com website. Tickets zijn samengesteld uit een reeks velden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4051,582,5,'Winnende nummers verwijzen naar een verzameling nummers van een willekeurig spel, getrokken op een willekeurige dag en op een definitieve manier gepubliceerd door de officile exploitant van de loterij.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4052,573,5,'Spelersaccount betekent een reeks speciale webpagina\'s voor geregistreerde gebruikers, die wordt gebruikt om hun identificatie- en toegangsgegevens te beheren op Euromillions.com, zoals toegang tot hun transactie- en ticketgeschiedenis op de Euromillions.com website.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4053,574,5,'Verzendtermijn of uitslag-tijd: de resterende tijd tot de Euromillions.com website deelname accepteert voor de volgende trekking van een bepaald spel. Het verzendtermijn is gebaseerd op de lokale tijd, lokale regels van het betreffende spel en de lokale openingstijden van de plaatselijke loterijen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4054,575,5,'Veld betekent een enkel, genummerd, klikbaar gebied om de nummers te selecteren waarmee de speler mee wil deelnemen. Een ticket is gemaakt van een reeks velden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4055,576,5,'Spel betekent een van de individuele spellen of groepsgames die beschikbaar zijn op de Euromillions.com website.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4056,577,5,' betekent de Euro-valuta.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4057,578,5,'Groepsspellen of syndicaten: een groep gebruikers die gezamenlijk met een bepaald aantal tickets meespelen en gezamenlijk de winsten delen op basis van de individuele regels van het bepaalde groepsspel.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4058,579,5,'EuroMillions.com betekent de officile website van EuroMillions.com, momenteel toegankelijk op https://euromillions.com. De officile taal van de website Euromillions.com is Engels.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4059,580,5,'Officieel resultaat betekent de winnende getallen, het aantal winnaars en prijzen binnen elke categorie, gepubliceerd door de officile exploitant van de loterij.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4060,565,5,'Algemene voorwaarden\r\n','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4061,566,5,'Lees deze Algemene Voorwaarden zorgvuldig door voordat u de website Euromillions.com (\'Website\') gebruikt. Door de website te gebruiken of te bezoeken, accepteren spelers deze voorwaarden. Wij hechten veel waarde aan het vertrouwen van onze klanten en moedigen u aan om de Algemene voorwaarden en essentile informatie over onze Website aandachtig te lezen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4062,700,5,'Informatie','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4063,701,5,'De website Euromillions.com biedt verschillende officile informatie over de wereldwijde toonaangevende loterijtrekkingen, waaronder winnende nummers, officile regels en officile resultaten.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4064,702,5,'De publicatie van de informatie op Euromillions.com wordt alleen als een service aan de deelnemers aangeboden. De informatiestroom in het besturingssysteem van Euromillions.com kan fouten bevatten die te goeder trouw zijn gemaakt.\r\n','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4065,713,5,'Intellectueel eigendom','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4066,714,5,'De website Euromillions.com is niet gerelateerd, verbonden of aangesloten bij een van de officile spelorganisatoren. Alle logo\'s en handelsmerken die op de website Euromillions.com worden gebruikt, zijn de eigendommen van dergelijke spelorganisatoren en Euromillions.com gebruikt ze alleen voor informatieve doeleinden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4067,715,5,'Alle rechten, inclusief de intellectuele eigendomsrechten (dwz octrooien, auteursrechten, handelsmerken, dienstmerken, logo\'s, handelsnamen, knowhow of andere intellectuele eigendommen of rechten) met betrekking tot Euromillions.com zijn het exclusieve eigendom van de website Euromillions.com. Niemand zal een van de rechten gebruiken zonder de uitdrukkelijke schriftelijke goedkeuring van Euromillions.com.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4068,716,5,'Niemand mag enig deel van deze Algemene voorwaarden of de regels van de Euromillions.com website en de daarin voorgestelde spellen kopiren of dupliceren, in welke vorm dan ook en in welke media dan ook.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4069,717,5,'Elke schending van de rechten van de Euromillions.com-website zal zorgvuldig worden behandeld en Euromillions.com zal zijn rechten uitoefenen tegen iedereen die dergelijke rechten zal schenden. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4070,583,5,'Invoering','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4071,584,5,'Het belangrijkste doel van de Euromillions.com-website is om haar geregistreerde gebruikers in staat te stellen deel te nemen zonder enige geografische beperking in games die op de Euromillions.com-website staan vermeld.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4072,585,5,'Wat Euromillions.com doet','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4073,586,5,'Klanten komen naar de Euromillions.com-website','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4074,587,5,'Klanten selecteren een loterij en de gewenste getallen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4075,588,5,'Euromillions.com plaatst en bewaakt de status en maakt de resultaten van loten bekend.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4076,589,5,'In het geval van winst helpt Euromillions.com bij het claimen van de prijs.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4077,590,5,'Om het hoofddoel te bereiken, biedt de website Euromillions.com een verscheidenheid aan extra diensten, zoals een spelers account voor de gebruikers, validatie van tickets, publicatie van de resultaten van de trekkingen en het verzamelen en doorsturen van prijzen naar de winnaars in gevallen waarin de verzameling winsten is geautoriseerd voor uitvoering door de Euromillions-website.com.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4078,591,5,'Wat Euromillions.com niet doet','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4079,592,5,'Euromillions.com organiseert geen loterijspellen. Het zijn bestaande, offline loterijen die onafhankelijk zijn van de website Euromillions.com of zijn bedrijfsentiteit. De deelnemers begrijpen en erkennen daarom dat de betaling na hun winsten afkomstig zullen zijn van de offline loterijbedrijven en niet rechtstreeks van Euromillions.com','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4080,593,5,'Euromillions.com neemt GEEN commissie in rekening over de winst als een vergoeding voor zijn service zolang de overname van de winst geen kosten met zich meebrengt voor Euromillions.com. Euromillions.com past alleen een redelijke opwaardering toe op de individuele ticketprijzen als een vergoeding voor haar service.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4081,594,5,'Geregistreerde gebruikers moeten de Algemene voorwaarden accepteren, niet alleen bij de registratie, maar telkens wanneer ze tickets in het spel kopen. In elk betaald spel hebben de deelnemers de kans om de bijgewerkte Algemene voorwaarden te bekijken en ermee akkoord te gaan. Met de acceptatie van de Algemene voorwaarden geven de deelnemers Euromillions.com toestemming om namens de offline loterijen voor hen op te treden en de winsten van de deelnemers op te eisen en te behandelen in alle gevallen waarin offline loterijen toestaan dat externen de winsten overnemen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4082,595,5,'De Euromillions.com-website heeft het recht om deze Algemene Voorwaarden te allen tijde te wijzigen, geheel naar eigen willen en zonder enige speciale kennisgeving. Deelnemers moeten echter elke keer dat ze de diensten van de site gebruiken akkoord gaan met de bijgewerkte Algemene voorwaarden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4083,596,5,'Deze Algemene Voorwaarden vormen de volledige overeenkomst tussen de website Euromillions.com en zijn geregistreerde gebruikers. Elke presentatie, belofte, toezegging of toestemming, zowel mondeling als schriftelijk, die niet voldoet aan de Algemene voorwaarden, is niet geldig.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4084,597,5,'In geval van geschillen worden de Engelse Algemene voorwaarden gebruikt als referentie.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4085,598,5,'In geval van discrepantie tussen deze Algemene Voorwaarden en de teksten op andere pagina\'s van de website, prevaleren deze Algemene Voorwaarden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4086,718,5,'Toepasselijk recht','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4087,719,5,'In elk geval van een verduidelijking, klacht, geschil, reservering, claim of actie als gevolg van deelname aan een activiteit op de Euromillions.com website, die niet is opgelost tussen de Euromillions.com website en de registrant, zal het geschil worden overgedragen aan de exclusieve beslissing van de wettelijke jurisdictie waar het bedrijf Euromillions.com is geregistreerd.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4088,720,5,'De rechtsverhouding tussen de geregistreerde gebruiker en Euromillions.com wordt beheerst door de wet op de rechtsmacht waar Euromillions.com is geregistreerd, met uitzondering van de regel inzake internationale burgerrechten. Alle geschillen en misverstanden in verband met het loterijspel of die voortvloeien uit het spel, worden alleen opgelost door een bevoegde rechtbank die rekening houdt met de plaatselijke en onderwerpregelgeving op de plaats waar Euromillions.com is geregistreerd.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4089,639,5,'Betaalde deelname','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4090,640,5,'Doel van het spel','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4091,641,5,'Het doel van het spel is om de winnende getallen te raden en deze te selecteren. Uiteindelijk zullen er door de officile exploitant van de loterij getallen getrokken worden en worden deze gepubliceerd. Spellen, hun prijzencategorien en prijzen worden bepaald volgens de publicaties van de officile exploitant van de loterij.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4092,642,5,'Deelnemers kunnen verschillende geldprijzen winnen, gelijk aan die toegekend in de officile loterijtrekkingen. Zoals vermeld in de vereisten voor deelname, is deelname aan een spel onderworpen aan en in overeenstemming met de officile en geldige regels van elk officieel loterijspel.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4093,643,5,'Deelname vereisten','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4094,644,5,'Geregistreerde gebruikers die voldoen aan alle noodzakelijke aspecten van de hierboven genoemde voorschriften en die geldige toegangsinformatie hebben, kunnen aan het spel deelnemen nadat ze voor kaartjes hebben betaald.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4095,645,5,'De Euromillions.com-website biedt van tijd tot tijd een welkomstbonus-pakket aan, waarbij het de eerste keer toegang krijgt tot een gratis geschenk of aanbieding. Inhoud, bedragen en voorwaarden in onderdelen of de welkomstbonus in zijn geheel kunnen onderhevig zijn aan onmiddellijke verandering of opschorting naar eigen goeddunken van de exploitant van de Euromillions.com-website.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4096,646,5,'Participatiestroom','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4097,647,5,'Deelname wordt voltooid door een ticket te kopen, dat wordt ingevuld volgens de officile regels van het betreffende loterijspel.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4098,648,5,'Volgens de regels van officile loterijtrekkingen kan de site Euromillions.com de mogelijkheid blokkeren om een kaartje te kopen voor de komende trekking. Tijdens deze blokkering zal Euromillions.com op dat moment stoppen met het aanbieden van zijn diensten in de aankomende trekking of stoppen met de mogelijkheid om kaarten te kopen voor de volgende trekking.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4099,649,5,'In het geval van een mislukte betaling, zal de Transactie status onder het Mijn Transacties menu worden geclassificeerd als \'Betaling Mislukt\', de Tickets van die transactie zullen niet verder worden verwerkt en zullen niet deelnemen aan eventuele trekkingen. Deze tickets worden niet getoond op de Tickets-pagina, omdat ze nooit zijn betaald.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4100,650,5,'In het geval van een succesvolle betaling:\r\n','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4101,651,5,'De betaling voor de Tickets wordt uitgevoerd door het spelersaccountsaldo te debiteren of door de creditcard van de deelnemer in rekening te brengen via de betalingsverwerker van de Euromillions.com-website. Betalingen en aankopen zijn definitief en kunnen niet worden geannuleerd of worden terugbetaald, omdat deze worden doorgestuurd. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4102,652,5,'Alleen tickets met de transactiestatus \'Betaald\' worden verder verwerkt.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4103,653,5,'Een ticket dat om welke reden dan ook, inclusief technische storingen, technische fouten, menselijke fouten, filtering van risicobeoordelingen, mogelijke fraudefilters, externe krachten of iets anders, niet met succes wordt geplaatst, wordt automatisch teruggestort op het spelersaccount van de deelnemer en geeft de deelnemer die het Ticket heeft ingevuld geen enkel recht, inclusief een beroep dat wordt ingediend voor de oorzaak van het falen of de gevolgen ervan.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4104,654,5,'De website Euromillions.com en zijn systeem hebben het recht om een ticket met terugwerkende kracht te annuleren en terug te halen, als door Euromillions.com wordt vastgesteld dat de plaatsing voor het Ticket om welke reden dan ook niet volledig is voltooid, inclusief maar niet beperkt tot technische storingen, technische falen, menselijke fouten, filtering van risicobeoordeling, mogelijke fraudefilters, externe krachten of enige andere reden. In een dergelijk geval wordt het ticket vanaf het begin als ongeldig beschouwd en wordt het beschouwd als teruggeroepen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4105,655,5,'Onmiddellijk nadat de winnende nummers, het aantal winnaars en de winnende bedragen in elk van de officile loterijtrekkingen worden gepubliceerd door de officile exploitant van de loterijtrekking (de \'officile resultaten\'), zal de Euromillions.com-website een uitgebreid onderzoek doen van alle geldige tickets. De Euromillions.com-website zal onderzoeken of er onder de geldige tickets selecties zijn die een deelnemer of deelnemers recht geven op prijzen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4106,656,5,'Groep Spellen','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4107,657,5,'De deelnamevoorschriften van de groep spellen, die van tijd tot tijd beschikbaar zijn op Euromillions.com, zijn op dezelfde manier beschreven als de individuele spellen in deze Algemene voorwaarden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4108,658,5,'De individuele regels van de bepaalde groep spellen staan op de webpagina van het spel op Euromillions.com.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4109,659,5,'Euromillions.com behoudt zich het recht voor om gedeeltelijke, onmiddellijke of toekomstige, volledige of gedeeltelijke wijzigingen of schorsingen geheel naar eigen goeddunken aan groep spellen te verlenen zonder de verplichting om de registranten van de Euromillions.com-website hiervan vooraf op de hoogte te stellen.','nl');
