INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4202,432,5,'Voor meer accurate statistieken over toegang tot onze site en om verschillende hulpprogramma\'s te testen die gebruiksvriendelijker en interessanter gebruik voor onze gebruikers garanderen, gebruiken we de service van derden <a href = \'https: //developers.google.com/analytics/devguides/ collection / analyticsjs / cookie-gebruik \'> Google Analytics </a>.','nl');
