INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4204,426,5,'Een cookie is een klein stukje tekst dat door de bezochte website naar uw browser wordt gestuurd. Dit helpt de site om informatie over uw bezoek te onthouden, bijvoorbeeld de taal die u hebt gekozen en nog veel meer. Dit maakt uw volgende bezoek gemakkelijker en de site is nuttiger voor u. Cookies spelen een belangrijke rol. Zonder cookies zou het gebruik van internet minder handig zijn.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4205,427,5,'We gebruiken cookies voor vele doeleinden, bijvoorbeeld:','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4206,425,5,'Wat zijn cookies en hoe gebruiken we ze?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4207,428,5,'<Strong> Instellingen </ strong>: dankzij deze cookies kan onze site informatie onthouden die het gedrag en het uiterlijk van het systeem helpt verbeteren. Het voorkomt dat u bijvoorbeeld niet telkens wanneer u de site bezoekt uw voorkeurstaal of valuta wordt gevraagd.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4208,429,5,'<strong> Beveiliging </ strong>: we gebruiken beveiligingscookies om gebruikers te verifiren, fraude bij het inloggen te voorkomen en gebruikersgegevens te beschermen tegen ongeautoriseerde partijen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4209,430,5,'<strong> Analytics </ strong>: we gebruiken analytische hulpprogramma\'s die ons helpen begrijpen hoe gebruikers omgaan met de site en we gebruiken een reeks cookies om informatie te verzamelen en statistieken over het gebruik van de website te genereren zonder personen persoonlijk te identificeren. ','nl');
