INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4219,744,5,'El Gordo is een Spaanse Kerstloterij waarvan de trekking op 22 december in Madrid plaatsvindt. El Gordo wordt beschouwd als de meest genereuze loterij ter wereld met een groots prijzenbedrag. De geschatte prijzenpot voor de trekking dit jaar bestaat uit het enorme bedrag van  2,3 MILJARD. Je kan nu eenvoudig mee spelen via EuroMillions.com en kans maken om de de El Gordo Kerstloterij te winnen voor slechts  25 per ticket.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4220,750,5,'De Kerstloterij heeft een specifieke spelmechaniek die voor nog veel meer prijzen zorgt. Je kunt dezelfde kaartjes 2 keer kopen. Bekijk hoeveel nummers beschikbaar zijn: <br>\r\n- Beschikbare nummers gaan van 00000 tot 99999 <br>\r\n- Voor elk nummer zijn er 170 beschikbare series (groepen) <br>\r\n- Elke serie bestaat uit 10 kaartjes (dit is wat je koopt) <br>\r\nKortom, voor ieder nummer zijn er 1.700 tickets te koop. Dat betekent dat maximaal 1.700 mensen de eerste prijs kunnen winnen! <br> Heb jij je tickets al gekocht? Controleer dan de <a href=\'/nl/kerst-loterij/uitslagen\'> laatste resultaten van de Kerstloterij en winnende series </a>!','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4221,749,5,'Hoeveel tickets zijn er te koop?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4222,746,5,'De tickets van de El Gordo Kerstloterij worden op een andere manier verkocht en verschilt van de manier die je van EuroMillions gewend bent. Het is heel eenvoudig: \r\n<ol style = \'margin-left: 30px;\'> <li> Kies je favoriete 5-cijferige nummerreeks van 00000 tot 99999, uit de beschikbare tickets. </ li>\r\n<li> Kies het aantal tickets waarmee je wilt spelen. </ li>\r\n<li> Nadat je de tickets hebt geselecteerd  klik je op Volgende om ze te betalen. </ li> </ ol>','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4223,745,5,'Hoe speel je mee met de Kerstloterij? ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4224,743,5,'El Gordo \'s werelds grootste Kerstloterij zal weer terug zijn in 2018','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4225,748,5,'<li> 1e prijs - 1 prijs van  400.000 voor elk ticket </ li>\r\n<li> 2e prijs - 1 prijs van  125.000 voor elk ticket </ li>\r\n<li> 3e prijs - 1 prijs van  50.000 voor elk ticket </ li>\r\n<li> 4e prijs - 2 prijzen van  20.000 voor elk ticket </ li>\r\n<li> 5e prijs - 8 prijzen van  6.000 voor elk ticket </ li>\r\n<li> 1.794 series van 5 correcte nummers winnen elk  1.000 </ li>\r\n<li> 9.999 overeenkomsten van het laatste El Gordo-cijfer winnen elk  200 voor de reeks','nl');
