INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4465,535,5,'Hoewel veel spelers van EuroMillions en Powerball vaak vasthouden aan hun favoriete nummers, geven anderen er de voorkeur aan ze elke keer te veranderen. Als je de Lucky Dip  optie wil proberen ga je naar onze Speel pagina en klik op Alle lijnen willekeurig, ons systeem genereert voor jou een willekeurige reeks nummers en Lucky Stars. De kansen om te winnen voor de gegenereerde lijnen zijn hetzelfde, maar er zijn meer kansen dat je de enige bent met de willekeurige reeks waardoor je de prijs met niemand hoeft te delen! ','nl');
