INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4481,558,5,'Als je niet meespeelt, kan je niet winnen! ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4482,554,5,'Selecteer vijf nummers van 1 tot 50 en twee Lucky Stars genummerd van 1 tot 12. De resultaten worden kort na de trekking gepubliceerd op EuroMillions.com en spelers ontvangen een e-mailmelding met de laatste resultaten als ze gewonnen hebben. Winsten zijn belastingvrij en worden onmiddellijk bijgeschreven op het account van de speler op Euromillions.com','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4483,555,5,'Elk persoon van 18 jaar of ouder kan EuroMillions spelen. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4484,556,5,'De EuroMillions Jackpot kan oplopen tot  190 miljoen met een gegarandeerde jackpot van minimaal  17 miljoen ( 14,4 miljoen) per trekking (<a href=\'https://euromillions.com/nl/informatie/euromillions-trekking\'>twee trekkingen per week</a>). Bij afwezigheid van een jackpot winnaar, wordt het geld doorgeschoven naar de volgende trekking en groeit het de jackpot totdat er wordt gewonnen, of de maximale jackpot ( 190 miljoen) wordt bereikt.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4485,557,5,'De EuroMillions-loterij heeft 13 prijscategorien en de kans om de EuroMillion-prijs te winnen is 1 op 22. Om de EuroMillions-jackpot te winnen, moeten spelers 5 hoofdnummers en twee Lucky Stars raden. De kans op het winnen van de EuroMillions jackpot is 1 op 139 838 800.','nl');
