INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4537,479,5,'Speel mee met je EuroMillions lot door <em>vijf</em> hoofdnummers van 1 tot 50 en <em>twee</em> Lucky Stars van 1 tot 12 te selecteren. Of klik op \'Alle lijnen willekeurig\' om een willekeurige selectie van getallen te kiezen.','nl');
