INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4539,484,5,'Er zijn 6 lijnen per speelstrook zichtbaar op standaard pc- of laptopschermen (of minder als je toegang hebt vanaf een kleiner scherm, mobiel of tablet). Om meer lijnen toe te voegen, klik je op \'Meer lijnen toevoegen\' waardoor er meer speelstroken verschijnen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4540,481,5,'Koop je tickets','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4541,485,5,'Ben je tevreden met de keuze van nummers klik dan op \'Volgende\' je wordt door het betalingsproces geleid.','nl');
