INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4543,486,5,'Na het voltooien van de betaling ontvangt je een bericht op de website met daarin de gekozen nummers. Je kunt alle lijnen en tickets die je hebt gekocht op elk moment in je spelers account zien.','nl');
