INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4545,487,5,'Na de trekking ontvangt je een e-mailmelding met de laatste resultaten en een melding of je iets gewonnen hebt. Alle winsten worden automatisch bijgeschreven op je spelers account.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4546,323,5,'Veelgestelde vragen','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4547,354,5,'EuroMillions is de grootste Europese transnationale loterij met een jackpot van minimaal 17 miljoen euro op elke dinsdag en vrijdag. De jackpot kan oplopen tot de 190 miljoen euro als er geen winnaars zijn.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4548,330,5,'Wat is de EuroMillions loterij?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4549,363,5,'Je kan mee spelen met meerdere toekomstige trekkingen van EuroMillions door Kies je pakket te selecteren onder de lijnen op de <a href=/nl/euromillions/speel> Spelpagina </a>. Je kunt van tevoren een ticket kopen voor maximaal 48 trekkingen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4550,339,5,'Hoe kan ik mee doen aan een toekomstige trekking?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4551,364,5,'Kort na elke trekking die je hebt gespeeld, ontvang je de <a href=\'https://euromillions.com/nl/euromillions/uitslagen\'>laatste EuroMillions resultaten</a> rechtstreeks in je e-mail. Je kan zelf instellen om altijd een mail te ontvangen met de trekking resultaten, zelfs als je nog niet hebt meegespeeld. Als alternatief kunnen spelers hun online spelers account of onze resultatenpagina bezoeken met de verdeling van de prijzen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4552,340,5,'Hoe weet ik of ik een prijs heb gewonnen?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4553,365,5,'Alle winsten worden kort na de trekking automatisch bijgeschreven op je spelersaccount. <a href=\'https://euromillions.com/nl/informatie/hoe-claim-je-een-loterij-prijs\'>Grote prijzen</a> worden direct overgemaakt naar uw priv-bankrekening zonder provisie.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4554,341,5,'Hoe kan ik mijn prijs claimen?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4555,366,5,'<a href=\'https://euromillions.com/nl/informatie/belastingregels-over-loterijprijzen\'>De meeste landen belasten de winst niet</a>. We raden u echter aan om te controleren of gokken belastbaar is in het land waar u woont.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4556,342,5,'Zijn de loterijwinsten belastbaar?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4557,367,5,'De kans om de EuroMillions jackpot te winnen is 1 op 139 838 160 . De kans om een EuroMillions prijs te winnen is 1 op 22.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4558,343,5,'Wat zijn de kansen om te winnen?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4559,368,5,'<a href=\'/nl/euromillions/speel\'> EuroMillions spelen </a> op EuroMillions.com kost slechts  3,00 ( 2,51) per spel, de beste prijs die je online kan vinden. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4560,344,5,'Hoeveel kost een EuroMillions ticket? ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4561,369,5,'Wij accepteren creditcards en andere betaalkaarten in alle valuta\'s. Transacties worden in euro\'s gemaakt.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4562,345,5,'Welke betalingsopties en valuta\'s worden geaccepteerd?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4563,370,5,'De kaartverkoop sluit om 19.00 uur Midden-Europese tijd op de dag van de trekking. Tickets die na 19.00 worden besteld, worden automatisch mee genomen naar de volgende trekking.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4564,346,5,'Hoe laat sluit de ticketverkoop? ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4565,371,5,'De EuroMillions Jackpot kan oplopen tot  190 miljoen met een gegarandeerde jackpot van minimaal  17 miljoen ( 14,4 miljoen) per trekking (twee trekkingen per week). Bij afwezigheid van een jackpot-winnaar, wordt het geld doorgeschoven naar de volgende trekking en groeit het totdat de jackpot wordt gewonnen, of de Euromillions maximum prijs ( 190 miljoen) wordt bereikt.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4566,347,5,'Heeft de jackpot van EuroMillions een maximum limiet? ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4567,372,5,'Onder het kopje Tickets  van je spelers account kun je de geschiedenis vinden van al je eerdere meegespeelde spellen. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4568,348,5,'Waar kan ik mijn eerder meegespeelde spellen terug vinden? ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4569,355,5,'EuroMillions.com is de eerste online loterij die is gebouwd om op elk apparaat en elke schermgrootte te werken. Mobiel of desktop, groot of klein, we zullen je altijd de beste gebruikerservaring bieden. Tijd is waardevol voor ons, dus we werken er hard aan om je een snelle, slimme en betrouwbare ervaring te bieden. De online loterij is overal te spelen, vanuit je luie stoel of onderweg het kan allemaal. We begrijpen wat spelers van ons verwachten en wij verzekeren dat de winst altijd belastingvrij zal zijn. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4570,331,5,'Wat is EuroMillions.com? ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4571,373,5,'U kunt ons een bericht sturen via ons <a href=\'/nl/contact\'> contactformulier </a> waar we vragen of u gedetailleerd uw probleem kan beschrijven, via welk type apparaat u speelt, welke browser u heeft en welk besturingssysteem u gebruikt. U kan ter verduidelijking een screenshot van het probleem bijvoegen. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4572,349,5,'Wat moet ik doen als ik technische problemen ondervind?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4573,374,5,'Als u uw wachtwoord bent vergeten, kunt u het <a href=\'/nl/wachtwoord-vergeten\'>eenvoudig opnieuw instellen</a>. Als uw account al is geblokkeerd, neemt u contact op met onze <a href=\'/nl/contact\'> klantenservice</a> om het probleem op te lossen.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4574,350,5,'Ik ben mijn wachtwoord vergeten en kan niet inloggen. Wat moet ik doen?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4575,375,5,'Via E-mailinstellingen van uw spelers account kunt u eenvoudig uw voorkeuren voor e-mailmeldingen aanpassen. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4576,351,5,'Hoe kan ik e-mailmeldingen uitschakelen? ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4577,376,5,'Ja. Iedereen van over de hele wereld kan meespelen via EuroMillions.com op voorwaarde dat het spelen van een buitenlandse loterij niet verboden is in het land waar je woont. ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4578,352,5,'Kan ik vanuit ieder land mee spelen met de EuroMillions loterij?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4579,377,5,'Om in aanmerking te komen voor het spelen van EuroMillions of andere loterijen, moeten alle deelnemers 18 jaar of ouder zijn (16+ in het VK).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4580,353,5,'Is er een minimum leeftijd om me te spelen met EuroMillions?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4581,356,5,'Nu kun je EuroMillions en de <a href=\'/nl/kerst-loterij/spelen\'>Spaanse Kerstloterij spelen</a>. Je vindt hier <a href=\'/nl/euromillions/speluitleg\'>hoe je EuroMillions kan spelen</a>.<br>Meld u aan om op de hoogte te blijven van de lancering van nieuwe games op onze website en ontdek intussen de <a href=\'https://euromillions.com/nl/informatie/loterij-speel\'>loterijen met de hoogste jackpots ter wereld</a> en lees alle <a href=\'/nl/powerball/speluitleg\'>informatie over de Powerball-loterij</a>.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4582,332,5,'Welke loterijen kan ik spelen?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4583,357,5,'<a href=\'https://euromillions.com/nl/informatie/euromillions-trekking\'>De EuroMillions trekkingen</a> vinden plaats op dinsdag- en vrijdagavond om ongeveer 21:00 uur MET.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4584,333,5,'Hoe laat vinden de trekkingen plaats?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4585,358,5,'Om de EuroMillions jackpot te winnen moeten de 5 gekozen getallen en 2 Lucky Stars matchen met de uitslag. Om kans te maken op een EuroMillions prijs moeten er minimaal 2 of meer getallen matchen. Zo makkelijk is het om een winnaar te zijn! ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4586,334,5,'Hoe win ik de jackpot? ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4587,359,5,'De laatste <a href=\'/nl/euromillions/uitslagen\'>EuroMillions resultaten </a> worden ongeveer een uur na de trekking om 22:30 uur CET bekendgemaakt. Een volledige verdeling van de prijzen worden een uur later 23:30 uur CET vrijgegeven. In geval van een grote jackpot kan de verwerking en verificatie van de laatste resultaten duren tot middernacht. U kunt de nieuwste EuroMillions resultaten op onze resultatenpagina vinden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4588,335,5,'Wanneer worden de trekking resultaten bekend gemaakt?','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4589,360,5,'De EuroMillions loterij heeft in totaal 13 prijscategorieen. De verdeling van de prijzen laat u het bedrag van de winst per prijsniveau zien. U kunt in <a href=\'/nl/euromillions/uitslagen\'>onze resultaten</a> een voorbeeld van de prijsverdeling zien.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4590,336,5,'Hoe worden de prijzen verdeeld? ','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4591,361,5,'De gegarandeerde minimum jackpot is 17 miljoen en kan oplopen tot 190 miljoen! ','nl');
