INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4593,362,5,'Een EuroMillions Superdraw is een speciale trekking die een gegarandeerde jackpot van  100 miljoen bevat, ongeacht of de EuroMillions jackpot al dan niet is gewonnen in de voorgaande trekking. Ze komen meestal een of twee keer per jaar voor bij een speciale gebeurtenis of om veranderingen in de loterij te creren, dit kan ieder willekeurig moment zijn. Als er net zoals bij een normale EuroMillions trekking niemand de jackpot wint, wordt de jackpot doorgeschoven naar de volgende trekking. ','nl');
