INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4768,980,5,'De Franse Lotto is een van de meest populaire spellen in Frankrijk en heeft een trekking op iedere maandag, woensdag en zaterdag om 20:00 CET (21:00 GMT).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4769,982,5,'De trekkingen van de nationale Oostenrijkse Lotto 6 aus 45 vinden twee keer per week plaats in Wien, op woensdag om 18:45 CET (17:45 GMT) en op zondag om 19:15 CET (18:15 GMT).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4770,984,5,'De trekkingen van de Poolse Lotto, de grootste loterij van het land, vinden elke dinsdag-, donderdag- en zaterdagavond om 21:40 uur CET (20:40 GMT) plaats.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4771,986,5,'Een van de meest populaire loterijen in Australi, de OZ Lotto, heeft een keer per week een trekking. Iedere dinsdagavond om 20:30 AEST (21:30 AEDT). Dat komt overeen met 11.30 uur CET van oktober tot maart en 12.30 uur CET voor de rest van het jaar.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4772,988,5,'De Powerball heeft zijn eigen versie in Australi, die OZ Powerball wordt genoemd. De trekking van deze grote loterij vindt elke donderdag plaats om 20:30 AEST (21:30 AEDT), wat overeenkomt met 11:30 CET van oktober tot maart en 12:30 uur CET voor de rest van het jaar.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4773,990,5,'De OZ Monday and Wednesday lotto zijn beide in veel delen van Australi bekend als Gold Lotto en X Lotto. De trekkingen worden twee keer per week gehouden op maandag- en woensdagavond om 20:30 AEST / 21:30 AEDT, (11:30 CET van oktober tot maart en 12:30 CET voor de rest van het jaar). ','nl');
