INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4778,964,5,'De trekkingen van de Pan-Europese loterij Eurojackpot worden elke week gehouden op vrijdagavond in Helsinki, Finland, om 21.00 uur lokale tijd (20.00 uur CET).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4779,966,5,'De trekkingen van Mega Millions, een van de grootste Amerikaanse loterijen, worden twee keer per week gehouden in de Amerikaanse stad Atlanta, Georgia, elke dinsdag en vrijdag om 23:00 Eastern Time (dat is 05:00 op woensdag en zaterdagochtend voor Centraal-Europa Tijd).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4780,968,5,'Superenalotto vindt drie keer per week plaats, elke dinsdag, donderdag en zaterdag, in Itali om 20:00 CET (19:00 GMT). Als de trekking samenvalt met een Italiaanse feestdag, wordt deze uitgesteld tot de volgende dag.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4781,970,5,'De Mega-sena is de meest populaire loterij in Brazili en hun trekkingen worden twee keer per week gehouden, elke woensdag en zaterdag, in Brasilia om 20:00, lokale tijd (23:00 CET).','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4782,972,5,'De Lotto 6aus49 is de belangrijkste nationale loterij in Duitsland waarvan de trekking elke woensdag om 18:25 CET (17:25 GMT) en elke zaterdag om 19:25 uur CET (18:25 GMT) plaatsvindt.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4783,974,5,'Alle trekkingen van Thunderball, een van de meest populaire loterijen in het Verenigd Koninkrijk, worden elke woensdag, vrijdag en zaterdag om 20:00 lokale tijd (21:00 CET) gehouden.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4784,976,5,'De UK Lotto-trekking vindt drie keer per week plaats, elke woensdag-, vrijdag- en zaterdagavond, in het Verenigd Koninkrijk om 20:30 GMT (21:30 CET), direct na de Thunderball-trekking.','nl');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (4785,949,5,'Wist je dat je de kans hebt om iedere dag van de week verschillende loterijen te winnen die in verschillende landen over de hele wereld worden plaatsvinden? Ontdek aan welke populaire loterijen je iedere dag van de week kan deelnemen en wanneer ze plaatsvinden. En vergeet niet dat elk jaar tijdens de kerstvakantie twee speciale trekkingen worden gehouden in Spanje: El Gordo Christmas Lottery op 22 december en El Nio Lottery op 6 januari!','nl');
