INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (639,354,1,'EuroMillions is the biggest European transnational lottery featuring a minimum 17 million euro jackpot every Tuesday and Friday. The jackpot can rollover until it reaches 190 million euro if there are no winners.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (640,354,2,'EuroMillones es la lotería transnacional más grande de Europa que cuenta con un bote mínimo de 17 millones de euros cada martes y viernes. El bote puede acumularse hasta llegar a un tope de 190 millones de euros, en caso de no haber ganadores.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (641,330,1,'What is the EuroMillions lottery?','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (642,330,2,'¿Qué es la lotería EuroMillones?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (643,363,1,'You can purchase a play for a future EuroMillions with a subscription to multiple draws, under your lines on the <a href=\'/euromillions/play\'>EuroMillions Play page</a>. You can buy a ticket for up to 48 draws in advance.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (644,363,2,'Puedes adquirir juegos para futuros sorteos de EuroMillones creando una subscripción a multiples sorteos, opción que está debajo de tus líneas en <a href=\'/es/euromillions/jugar\'>la página de juego de EuroMillones</a>. Puedes comprar un boleto hasta para 48 sorteos de anticipación.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (645,339,1,'How can I participate in a future draw?','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (646,339,2,'¿Cómo puedo participar en un próximo sorteo?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (647,364,1,'Shortly after each draw that you played you will receive the <a href=\'/euromillions/results\'>latest EuroMillions results</a> directly in your email. You can even set from your Account area to send always an email with the draw results even if you have not played. Alternatively, players can visit their online players account or our Results Page with the prize breakdown.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (648,364,2,'Poco después del sorteo que has jugado, recibirás los <a href=\'/es/euromillions/resultados\'>últimos resultados de EuroMillones</a> directamente en tu correo electrónico. Puedes configurar tu cuenta para siempre recibir notificaciones con los resultados de los sorteos, incluso aquellos en los que no has participado. También los jugadores pueden revisar sus cuentas de jugador o nuestra página de resultados con el desglose de premios.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (649,340,1,'How do I know if I have won a prize?','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (650,340,2,'¿Cómo sé que he ganado un premio?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (651,365,1,'All winnings are automatically credited to your player account shortly after the draw. <a href=\'https://euromillions.com/en/facts/how-to-claim-lottery-prize\'>Big prizes</a> are directly transferred to your private bank account without any commissions.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (652,365,2,'Todos los premios son automáticamente acreditados en tu cuenta de jugador poco después del sorteo. <a href=\'https://euromillions.com/es/datos/como-reclamar-premios-loteria\'>Los premios grandes</a> son directamente transferidos a tu cuenta bancaria privada sin ninguna comisión.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (653,341,1,'How do I claim a prize?','en');
