INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (655,366,1,'Most countries do not tax winnings. However, we advise you to check whether <a href=\'https://euromillions.com/en/facts/lottery-prizes-tax-regulations\'>gambling is taxable in your country of residence</a>.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (656,366,2,'La mayoría de países no aplican impuestos a premios. Sin embargo, te aconsejamos verificar si los <a href=\'https://euromillions.com/es/datos/regulaciones-impuestos-premios-loteria\'>juegos están sujetos a impuestos en tu país de residencia</a>.','es');
