INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (659,367,1,'The odds of winning the EuroMillions jackpot are 139 838 160 to 1. The odds of winning the minimum EuroMillions prize are 1 in 22. Compare them with the <a href=\'https://euromillions.com/en/facts/top-lotteries--odds-of-winning\'>chances of winning other international lotteries</a>!','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (660,367,2,'Las probabilidades de ganar el bote de EuroMillones son 139 838 160 en 1. Las probabilidades de ganar el premio más bajo de EuroMillones son de 1 entre 22. Compáralas con <a href=\'https://euromillions.com/es/datos/oportunidad-de-ganar-loterias-importantes\'>las estadisticas de otras loterías internacionales</a>.','es');
