INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (664,368,2,'<a href=\'/es/euromillions/jugar\'>Jugar EuroMillones</a> en EuroMillions.com solo cuesta €3.00 ($3.54) por juego, el mejor precio disponible en Internet.','es');
