INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (671,370,1,'Ticket sales close at 19:00 Central European Time the day of the draw. Bets ordered after the cut of time will be validated for the next draw.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (672,370,2,'La venta de boletos cierra el mismo días del sorteo a las 19:00 Hora de Europa central. Las apuestas ordenadas después de ese plazo serán validadas para el siguiente sorteo.','es');
