INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (675,371,1,'The jackpot can rollover until the €190 million jmaximum amount is reached. A €190 million jackpot would play only for two consecutive draws and if there is no jackpot winner the big prize will cascade down to the following prize tier that features at least a winner.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (676,371,2,'El bote puede acumularse hasta que se alcance el máximo de €190 millones. Un bote de €190 millones puede mantenerse solo dos sorteos consecutivos y si no hay ganador, el gran premio se repartirá entre los acertantes de la siguiente categoría de premios.','es');
