INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (683,355,1,'EuroMillions.com is the first lottery based website built to work on every device and every screen size, no matter how large or small. Mobile or desktop, we will always offer you the best user experience. Your time is valuable to us, so we work hard to provide you with a quick, smart, and reliable experience to play lottery online from the comfort of your home or on the go. Your fate-changer might be right here in the palm of your hand. We understand what you expect from us and we assure you that your winnings are commission free and will remain so forever.\r\n','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (684,355,2,'EuroMillions.com es el primer sitio web de juegos de lotería diseñado para trabajar en cada dispositivo y en cada pantalla, sin importar su tamaño. Móvil o de escritorio, siempre ofrecemos la mejor experiencia al usuario. Tu tiempo es valioso para nosotros, por lo que nos esforzamos en proveerte una experiencia rápida, inteligente y confiable para jugar la lotería en línea desde la comodidad de tu casa o donde lo prefieras. El mayor cambio de tu vida se encuentra directamente en la palma de tu mano. Entendemos qué esperas de nosotros y te aseguramos que todas tus ganancias son libres de comisión y así se mantendrá por siempre.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (685,331,1,'What is EuroMillions.com?','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (686,331,2,'¿Qué es EuroMillions.com?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (687,373,1,'You can send us a message through our <a href=\'/contact\'>contact form</a> describing in detail your problem, which kind of device, browser and operative system you are using, and possibly provide a screenshot of the issue.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (688,373,2,'Debes enviarnos un mensaje a través de nuestro <a href=\'/es/contacto\'>formulario de contacto</a> describiendo en detalle tu problema, qué tipo de dispositivo, buscador y sistema operativo estás utilizando y posiblemente una captura de pantalla del problema.','es');
