INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (690,349,2,'¿Qué debo hacer en caso de experimentar problemas técnicos?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (691,374,1,'If you have forgotten your password you can <a href=\'/user-access/forgotPassword\'>easily reset it</a>. If your account has already been blocked please contact our <a href=\'/contact\'>customer support</a> to resolve the problem.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (692,374,2,'Si has olvidado tu contraseña la puedes <a href=\'/es/recuperar-contrasena\'>restablecer fácilmente</a>. Si tu cuenta ha sido bloqueada, por favor contacta con nuestro <a href=\'/es/contacto\'>soporte al cliente</a> para resolver el problema.','es');
