INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (699,376,1,'Yes. Anyone from around the world can play through EuroMillions.com on the condition that playing a foreign lottery is not prohibited in their country of residence.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (700,376,2,'Si. Cualquiera puede jugar en EuroMillions.com desde cualquier parte del mundo, con la condición de que jugar una lotería extranjera no esté prohibido en su país de residencia.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (701,352,1,'Can I play lotto on EuroMillions.com from any country?','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (702,352,2,'¿Puedo jugar la lotería en EuroMillions.com desde cualquier país?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (703,377,1,'To be eligible to play EuroMillions or other lotteries, all participants must be 18 years or over (16+ in the UK).','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (704,377,2,'Para poder jugar EuroMillones u otras loterías, todos los participantes deben tener 18 años de edad o más (16+ en Reino Unido).','es');
