INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (707,356,1,'Now you can play EuroMillions and the <a href=\'/christmas-lottery/play\'>Spanish Christmas Lottery</a>. You can find out here <a href=\'/euromillions/help\'>how to play EuroMillions</a>.<br>Sign up to stay tuned with the launch of new games on our website and in the meanwhile discover the <a href=\'https://euromillions.com/en/facts/lottery-play\'>lotteries with highest jackpots in the world</a> and read all the <a href=\'/powerball/how-to-play\'>information about the Powerball lottery</a>.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (708,356,2,'Ahora puedes jugar EuroMillones y la <a href=\'/es/loteria-navidad/jugar\'>Lotería de Navidad</a>. Puedes descubrir aquí <a href=\'/es/euromillions/como-se-juega\'>cómo jugar EuroMillones</a>.<br>Regístrate para ser el primero en saber de lanzamiento de nuevos juegos en nuestra web y mientras tanto descubre las <a href=\'https://euromillions.com/es/datos/loterIas-jugar\'>loterías con los botes más altos del mundo</a> y lee toda la <a href=\'/es/powerball/como-se-juega\'>información sobre la lotería Powerball</a>.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (709,332,1,'What lotteries can I play?','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (710,332,2,'¿Qué loterías puedo jugar?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (711,357,1,'<a href=\'https://euromillions.com/en/facts/euromillions-drawing-times\'>EuroMillions draws</a> take place on Tuesday and Friday evenings at 21:00 CET (20:00 GMT) in Paris. Check out the difference with your time zone to know what time they play in your country and don\'t miss the opportunity to buy the tickets for any draw!','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (712,357,2,'<a href=\'https://euromillions.com/es/datos/euromillones-horario-sorteo\'>Los sorteos de EuroMillones</a> se realizan dos veces por semana los martes y viernes por la noche, a las 21:00 (CET) en París. El sorteo se acaba alrededor de las 22:30 (CET).','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (713,333,1,'What time are the draws?','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (714,333,2,'¿A qué hora son los sorteos de EuroMillones?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (715,358,1,'To win the EuroMillions jackpot players need to match all 5 main numbers and 2 Lucky Stars. But to win the minimum EuroMillions prize, players just have to match 2 or more numbers. It is that easy to be a winner!','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (716,358,2,'Para ganar el bote de EuroMillones, los jugadores deben acertar todos los 5 números principales y las 2 estrellas, pero para ganar el premio mínimo de EuroMillones, los jugadores solo deben acertar 2 o más números. ¡Es muy fácil ser un ganador!','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (717,334,1,'How do I win the jackpot?','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (718,334,2,'¿Cómo puedo ganar el bote?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (719,359,1,'The latest <a href=\'/euromillions/results\'>EuroMillions results</a> are revealed approximately an hour following the draw 22:30 CET. A full breakdown of prizes is released another hour later 23:30 CET. However, in the event of a large jackpot the processing and verification of the latest results can take up until midnight to finalise. You can get the latest Euromillions results on our results page.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (720,359,2,'Los últimos <a href=\'/es/euromillions/resultados\'>resultados de EuroMillones</a> se revelan aproximadamente una hora después del sorteo, 22:30 CET. El listado completo de premios es publicado otra hora después, 23:30 CET. Sin embargo, en el evento de un gran bote, el proceso de verificación de los últimos resultados puede finalizar hasta la medianoche. Los resultados más recientes de Euromillones están disponibles en nuestra página de resultados.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (721,335,1,'When are the draw results released?','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (722,335,2,'¿Cuándo se publican los resultados de los sorteos?','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (723,360,1,'The EuroMillions lottery has a total of 13 prize tiers. The highest prize is won matching the 5 main numbers and the 2 Lucky stars, while the smallest just with 2 out 5 main numbers. You can check out the <a href=\'/euromillions/results\'>prize breakdown of the latest draw in our results page</a>, with the amount of winners and money won per prize tier, as well as the prize breakdown of <a href=\'/euromillions/results/draw-history-page\'>all past EuroMillions draws</a>.','en');
