INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (881,428,1,'<strong>Preferences</strong> - These cookies allow our websites to remember information that changes the way the site behaves or looks, such as your preferred language or currency.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (882,428,2,'<strong>Preferencias</strong> - Estas cookies permiten a nuestras páginas web recordar información que cambie la forma que el sitio funcione o se vea, tales como tu idioma o moneda de preferencia. ','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (883,429,1,'<strong>Security</strong> - We use security cookies to authenticate users, prevent fraudulent use of login credentials, and protect user data from unauthorized parties.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (884,429,2,'<strong>Seguridad</strong> - Usamos cookies de seguridad para autenticar usuarios, prevenir uso fraudulento de credenciales de ingreso, y proteger los datos de usuario de terceras partes no autorizadas. ','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (885,430,1,'<strong>Analytics</strong> - We use analytics tools that help us understand how visitors engage with the website, and we use a set of cookies to collect information and report website usage statistics without personally identifying individual visitors.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (886,430,2,'<strong>Analítica web</strong> - Usamos herramientas de analítica web que nos ayudan a entender cómo los visitantes se acoplan al sitio web, y usamos un conjunto de cookies para recolectar información e informar las estadísticas de uso del sitio web sin identificar personalmente a visitantes individuales. ','es');
