INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (889,450,1,'Euromillions.com is the sole owner of the information collected at various points on this website. We will not sell, share, or rent this information to third parties in ways that are not disclosed in this statement.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (890,450,2,'EuroMillions.com es el único propietario de la información recopilada en varios puntos en este sitio web. No venderemos, compartiremos o alquilaremos esta información a terceras partes en maneras que no estén reveladas en esta declaración. ','es');
