INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (909,452,1,'In order to initiate a purchase assignment on this website, users have to register first. During registration a user is required to provide his/her contact information (such as email address and password). This information is used to contact, bill and identify the user in regard to services on our site.','en');
