INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1101,556,1,'The EuroMillions Jackpot can reach up to €190 million with a guaranteed jackpot of at least €17 million (£14.4 million) per draw (<a href=\'https://euromillions.com/en/facts/euromillions-drawing-times\'>two draws per week</a>). In the absence of a jackpot winner, the money is rolled over to the next draw and will grow until the jackpot is won, or the Euromillions Pool Cap (€190 million) is reached.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1102,556,2,'El bote de EuroMillones puede alcanzar el tope de €190 Millones con un mínimo garantizado de al menos €17 Millones ($19 Millones) por sorteo (<a href=\'https://euromillions.com/es/datos/euromillones-horario-sorteo\'>dos sorteos a la semana</a>). Si hay ausencia de un ganador del bote, el dinero se acumula para el siguiente sorteo y crecerá hasta que el bote lo gane alguien, o se alcance el tope de EuroMillones (€190 Millones).','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1103,557,1,'The EuroMillions lottery features 13 different tiers and the odds of winning the minimum EuroMillions prize are 1 in 22. To win the Euromillions jackpot prize, players need to match 5 main numbers from 50, and 2 lucky stars from 12. The odds of hitting the Euromillions Jackpot are 1 in 139 838 800.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1104,557,2,'La Lotería EuroMillones cuenta con 13 diferentes niveles y las oportunidades de ganar el menor premio de EuroMillones son 1 entre 22. Para ganar el bote, los jugadores necesitan acertar 5 números principales de 50 y 2 estrellas de 12. Los probabilidades de obtener el bote de EuroMillones son de 1 entre 139 838 800.','es');
