INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1129,573,1,'Player account means a set of dedicated webpages for registered users, used to manage their identification and access data at Euromillions.com, such as accessing their transaction and ticket history in the Euromillions.com website.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1130,573,2,'Cuenta de jugador es un conjunto de páginas web para jugadores registrados, se usan para administrar su identificación y datos de acceso a EuroMillions.com, así como para sus transacciones e historial de juego en el sitio web de EuroMillions.com.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1131,574,1,'Dispatch deadline or Cut off time means the remaining time until the Euromillions.com website accepts bets for the next draw for a certain game. The Dispatch deadline is based on the local time, local rules of the certain game, and the local opening hours of the local lottery shops.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1132,574,2,'La fecha límite es el tiempo restante en el cual el sitio web de EuroMillions.com acepta apuestas para el siguiente sorteo de un determinado juego. La fecha límite está basada en la hora local, las reglas locales de un determinado juego, y la hora de operación en las tiendas locales de lotería.','es');
