INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1171,590,1,'To fulfill its main purpose, the Euromillions.com website provides a variety of additional services, such as providing users a player account, validation of tickets, publishing results of the draws, and collecting and forwarding prizes to the winners in cases where collection is allowed to be executed by the Euromillions.com website.','en');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1172,590,2,'Para alcanzar su objetivo principal, el sitio web de EuroMillions.com ofrece una variedad de servicios adicionales, como proporcionar a los usuarios una cuenta de jugador, validación de boletos, publicación de resultados de los sorteos, y la recaudación y entrega de premios a los ganadores en casos donde dicha recaudación haya sido autorizada para ejecutarse por el sitio web de EuroMillions.com.','es');
INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1173,591,1,'What Euromillions.com Does Not','en');
