INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (1948,535,3,'В то время, как многие игроки ЕвроМиллион и Powerball всегда выбирают свои любимые номера, другие предпочитают менять их каждый раз или положиться на генератор случайных чисел. Если вы хотите попробовать вариант \'Мне повезёт’, просто перейдите на нашу страницу Играть и нажмите \'Генератор случайных чисел\', наша система создаст для вас случайную последовательность шаров и счастливых звезд для каждого поля. Шансы попадания для каждого сгенерированного поля совершенно одинаковые! Кроме того, есть еще один плюс!Возможно вы единственный, кто выбрал вариант со случайными числами в данный момент, и вам не придётся делиться призом с кем-либо ещё!\r\n','ru');
