INSERT INTO `translation_details` (`id`,`translation_id`,`language_id`,`value`,`lang`) VALUES (2000,697,3,'Все конвертации различных валют в ЕВРО будут осуществляться с использованием текущего обменного курса, публикуемого банковскими учреждениями.','ru');
